export function getAllResources() {
  const blogPosts = getBlogPosts();
  // const industryInsights = getIndustryInsights();
  // const successStories = getSuccessStories();

  // Combine all resources into one array
  const allResources = [
    ...blogPosts,
    // ...industryInsights,
    // ...successStories,
  ];

  // Sort the resources by published date in descending order
  const sortedResources = allResources.sort((a, b) => {
    const dateA = new Date(a.published_at);
    const dateB = new Date(b.published_at);
    return dateB - dateA; // Descending order
  });

  return sortedResources;
}

export function getResourceBySlug(slug) {
  // Get all resources
  const allResources = getAllResources();

  // Find the resource with the matching slug
  return allResources.find(item => item.slug === slug);
}
